mapper.update:
UPDATE PETSITTER_INFO
   SET EXPOSURE = #{exposure}
 WHERE USER_NO = #{userNo} AND PETSITTER_NO =#{petSitterNo}

UPDATE PETSITTER_INFO
   SET EXPOSURE = 'true',
       NIGHT_CHECKIN = #{nightCheckin},
       NIGHT_CHECKOUT = #{nightCheckout}
 WHERE USER_NO = #{userNo} AND PETSITTER_NO = #{petSitterNo}
